package com.oracle.packt.chp2.basics;


public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * Whole numbers
		 * Byte data type
		 * 8 bits in size
		 * Min value -128 (-2 ^ 7)
		 * Max value 127 (2 ^ 7 -  1)
		 */
		byte varByte = 9;
		
		/*
		 * Whole numbers
		 * Short data type
		 * 16 bits in size
		 * Min value -32,768 (-2 ^ 15)
		 * Max value 32,767 (2 ^ 15 -  1)
		 */
		short varShort = 9;
		
		/*
		 * Whole numbers
		 * Integer data type
		 * 32 bits in size
		 * Min value -2,147,483,648 (-2 ^ 31)
		 * Max value 2,147,483,647 (2 ^ 31 -  1)
		 */
		int varInt = 9;
		
		/*
		 * Whole numbers
		 * Long data type
		 * 64 bits in size
		 * Min value -9,223,372,036,854,775,808 (-2 ^ 63)
		 * Max value 9,223,372,036,854,775,807 (2 ^ 63 -  1)
		 */
		long varLong = 9;
		
		/*
		 * Decimal numbers
		 * float data type
		 * 32 bits in size
		 */
		float varFloat = 9.0f;
		
		/*
		 * Decimal numbers
		 * double data type
		 * 64 bits in size
		 */
		double varDouble = 9.0d;
		
		/*
		 * boolean data type
		 * true or false (1 or 0)
		 * 1 bit in size
		 */
		boolean varBool = true;
		
		/*
		 * character data type
		 * 16 bits in size
		 * ASCII Table
		 */
		char varChar = 'A';
		
		/*
		 * string data type
		 */
		String varString = "Hello World";
		
		
		System.out.println(varByte);
		System.out.println(varShort);
		System.out.println(varInt);
		System.out.println(varLong);
		System.out.println(varFloat);
		System.out.println(varDouble);
		System.out.println(varBool);
		System.out.println(varChar);
		System.out.println(varString);
	}

}
